mapper.select:
select detail_no, detail1, image1_attachtype, image1_attachoname, image1_attachsname, items_items_no
from items_images
where items_items_no = #{itemsItemsNo}

select detail_no, detail1, image1_attachtype, image1_attachoname, image1_attachsname, items_items_no
from items_images
where detail_no = #{detailNo}

mapper.update:
update items
set items_name = #{itemsName}, items_company = #{itemsCompany},	items_price = #{itemsPrice}, 
items_color= #{itemsColor}, items_option= #{itemsOption}, items_stock =	#{itemsStock}, 
items_model = #{itemsModel}, items_manufacture = #{itemsManufacture}, items_origin = #{itemsOrigin}

where items_no = #{itemsNo}
